	if err = randomize.Struct(seed, o, pollsContinentDBTypes, true, pollsContinentPrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize PollsContinent struct: %s", err)
	}

	// Remove Primary keys and unique columns from what we plan to update
	var fields []string
	if strmangle.StringSliceMatch(pollsContinentColumns, pollsContinentPrimaryKeyColumns) {
		fields = pollsContinentColumns
	} else {
		fields = strmangle.SetComplement(
			pollsContinentColumns,
			pollsContinentPrimaryKeyColumns,
		)
	}

	value := reflect.Indirect(reflect.ValueOf(o))
	typ := reflect.TypeOf(o).Elem()
	n := typ.NumField()

	updateMap := M{}
	for _, col := range fields {
		for i := 0; i < n; i++ {
			f := typ.Field(i)
			if f.Tag.Get("boil") == col {
				updateMap[col] = value.Field(i).Interface()
			}
		}
	}

	slice := PollsContinentSlice{o}
	if rowsAff, err := slice.UpdateAll(ctx, tx, updateMap); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("wanted one record updated but got", rowsAff)
	}
